import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import '../../app/router.dart';import 'marquee_widget.dart';class CustomAppBar extends StatelessWidget {  final String titleName;  const CustomAppBar({super.key, required this.titleName});  @override  Widget build(BuildContext context) {    return Card(      elevation: 2.0, // cardElevation      shape: RoundedRectangleBorder(        borderRadius: BorderRadius.circular(0.0),      ),      child: Container(        height: kToolbarHeight,        child: Row(          children: [            InkWell(              onTap: () {                finish(context);              },              child: Container(                padding: EdgeInsets.all(16.0),                child: Icon(Icons.close),              ),            ),            Expanded(              child: SingleChildScrollView(                padding: EdgeInsets.only(right: 16.0),                scrollDirection: Axis.horizontal,                child: MarqueeWidget(                  direction: Axis.horizontal,                    child: Text(                  titleName,                  style: TextStyle(                    fontFamily: 'Montserrat',                    color: Colors.black,                    fontSize: 16.0,                    fontWeight: FontWeight.w600,                  ),                )),              ),            ),            Container(              padding: EdgeInsets.all(16.0),              child: Transform.rotate(                  angle: 3.14159265359,                  child: Image.asset("assets/images/back_btn.png")),            )            // Add other widgets as needed          ],        ),      ),    );  }}