// dao/lesson_dao.dartimport 'package:floor/floor.dart';import '../features/signup/model/user_data.dart';@daoabstract class MyDao {  // UserData  @Query('SELECT * FROM UserData')  Future<List<UserData>> getAllLesson();  @Insert(onConflict: OnConflictStrategy.replace)  Future<void> insertUserData(List<UserData> lesson);  @Query("DELETE FROM UserData")  Future<void> deleteUsers();}