import 'dart:convert';import 'package:floor/floor.dart';@entityclass UserData {  @primaryKey  int? id;  String? name;  String? phone;  UserData({required this.id, required this.name, required this.phone});  UserData.fromJson(Map<String, dynamic> json) {    id = json['id'] ?? 0;    name = json['name'] ?? '';    phone = json['phone'] ?? '';  }  Map<String, dynamic> toJson() {    final Map<String, dynamic> data = <String, dynamic>{};    data['id'] = id;    data['name'] = name;    data['phone'] = phone;    return data;  }}